INSERT INTO `t_blog` VALUES ('80', '600多个微信小程序源码涵盖各个行业大部分带后台持续更新中', '今天把本人花费一个月左右整理的600多款小程序源码发布出来，以后每个月还会持续更新，很大一部分都带有后台源码并包含有小程序上线教程，帮你分分钟上线自己的小程序，下面我们来看看目录截图，最后面会把源码目录以文字的形式导出来，这样你可以在网页上搜索单个想要的小程序源码，看看有没有符合你需要的。', '2018-09-11 22:26:08', '14', '0', '<p><span style=\"color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; text-align: justify; text-indent: 32px; background-color: rgb(255, 255, 255);\">今天把本人花费一个月左右整理的600多款小程序源码发布出来，以后每个月还会持续更新，很大一部分都带有后台源码</span><span style=\"font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; color: rgb(79, 79, 79); text-align: justify; text-indent: 32px;\">并包含有小程序上线教程，帮你分分钟上线自己的小程序</span><span style=\"color: rgb(79, 79, 79); font-family: &quot;PingFang SC&quot;, &quot;Microsoft YaHei&quot;, SimHei, Arial, SimSun; text-align: justify; text-indent: 32px; background-color: rgb(255, 255, 255);\">，下面我们来看看目录截图，最后面会把源码目录以文字的形式导出来，这样你可以在网页上搜索单个想要的小程序源码，看看有没有符合你需要的。</span></p>', '5', '小程序源码');
INSERT INTO `t_blog` VALUES ('84', '我是一个java代码块', 'package com.ischoolbar.programmer.controller.admin;import java.util.HashMap;import java.util.List;import java.util.Map;import javax.annotation.Resource;imp', '2018-09-12 11:36:53', '8', '0', '<pre class=\"brush:java;toolbar:false\">package&nbsp;com.ischoolbar.programmer.controller.admin;\n\n\nimport&nbsp;java.util.HashMap;\nimport&nbsp;java.util.List;\nimport&nbsp;java.util.Map;\n\nimport&nbsp;javax.annotation.Resource;\nimport&nbsp;javax.servlet.http.HttpServletResponse;\n\nimport&nbsp;org.springframework.stereotype.Controller;\nimport&nbsp;org.springframework.web.bind.annotation.RequestMapping;\nimport&nbsp;org.springframework.web.bind.annotation.RequestParam;\n\nimport&nbsp;com.ischoolbar.programmer.entity.Blog;\nimport&nbsp;com.ischoolbar.programmer.entity.PageBean;\nimport&nbsp;com.ischoolbar.programmer.lucene.BlogIndex;\nimport&nbsp;com.ischoolbar.programmer.service.BlogService;\nimport&nbsp;com.ischoolbar.programmer.util.ResponseUtil;\nimport&nbsp;com.ischoolbar.programmer.util.StringUtil;\n\nimport&nbsp;net.sf.json.JSONArray;\nimport&nbsp;net.sf.json.JSONObject;\nimport&nbsp;net.sf.json.JsonConfig;\n\n\n/**\n&nbsp;*&nbsp;管理员博客Controller层\n&nbsp;*&nbsp;@author&nbsp;Administrator\n&nbsp;*\n&nbsp;*/\n@Controller\n@RequestMapping(&quot;/admin/blog&quot;)\npublic&nbsp;class&nbsp;BlogAdminController&nbsp;{\n\n	@Resource\n	private&nbsp;BlogService&nbsp;blogService;\n	\n	//&nbsp;博客索引\n	private&nbsp;BlogIndex&nbsp;blogIndex=new&nbsp;BlogIndex();\n	\n	/**\n	&nbsp;*&nbsp;添加或者修改博客信息\n	&nbsp;*&nbsp;@param&nbsp;blog\n	&nbsp;*&nbsp;@param&nbsp;response\n	&nbsp;*&nbsp;@return\n	&nbsp;*&nbsp;@throws&nbsp;Exception\n	&nbsp;*/\n	@RequestMapping(&quot;/save&quot;)\n	public&nbsp;String&nbsp;save(Blog&nbsp;blog,HttpServletResponse&nbsp;response)throws&nbsp;Exception{\n		int&nbsp;resultTotal=0;&nbsp;//&nbsp;操作的记录条数\n		if(blog.getId()==null){\n			resultTotal=blogService.add(blog);\n			blogIndex.addIndex(blog);&nbsp;//&nbsp;添加博客索引\n		}else{\n			resultTotal=blogService.update(blog);\n			blogIndex.updateIndex(blog);&nbsp;//&nbsp;更新博客索引\n		}\n		JSONObject&nbsp;result=new&nbsp;JSONObject();\n		if(resultTotal&gt;0){\n			result.put(&quot;success&quot;,&nbsp;true);\n		}else{\n			result.put(&quot;success&quot;,&nbsp;false);\n		}\n		ResponseUtil.write(response,&nbsp;result);\n		return&nbsp;null;\n	}\n	\n	/**\n	&nbsp;*&nbsp;分页查询博客信息\n	&nbsp;*&nbsp;@param&nbsp;page\n	&nbsp;*&nbsp;@param&nbsp;rows\n	&nbsp;*&nbsp;@param&nbsp;s_customer\n	&nbsp;*&nbsp;@param&nbsp;response\n	&nbsp;*&nbsp;@return\n	&nbsp;*&nbsp;@throws&nbsp;Exception\n	&nbsp;*/\n	@RequestMapping(&quot;/list&quot;)\n	public&nbsp;String&nbsp;list(@RequestParam(value=&quot;page&quot;,required=false)String&nbsp;page,\n			@RequestParam(value=&quot;rows&quot;,required=false)\n	String&nbsp;rows,Blog&nbsp;s_blog,HttpServletResponse&nbsp;response)throws&nbsp;Exception{\n		PageBean&nbsp;pageBean=new&nbsp;PageBean(Integer.parseInt(page),Integer.parseInt(rows));\n		Map&lt;String,Object&gt;&nbsp;map=new&nbsp;HashMap&lt;String,Object&gt;();\n		map.put(&quot;title&quot;,&nbsp;StringUtil.formatLike(s_blog.getTitle()));\n		map.put(&quot;start&quot;,&nbsp;pageBean.getStart());\n		map.put(&quot;size&quot;,&nbsp;pageBean.getPageSize());\n		List&lt;Blog&gt;&nbsp;blogList=blogService.list(map);\n		Long&nbsp;total=blogService.getTotal(map);\n		JSONObject&nbsp;result=new&nbsp;JSONObject();\n		JsonConfig&nbsp;jsonConfig=new&nbsp;JsonConfig();\n		jsonConfig.registerJsonValueProcessor(java.util.Date.class,&nbsp;\n				new&nbsp;DateJsonValueProcessor(&quot;yyyy-MM-dd&quot;));\n		JSONArray&nbsp;jsonArray=JSONArray.fromObject(blogList,jsonConfig);\n		result.put(&quot;rows&quot;,&nbsp;jsonArray);\n		result.put(&quot;total&quot;,&nbsp;total);\n		ResponseUtil.write(response,&nbsp;result);\n		return&nbsp;null;\n	}\n	\n	/**\n	&nbsp;*&nbsp;删除博客信息\n	&nbsp;*&nbsp;@param&nbsp;ids\n	&nbsp;*&nbsp;@param&nbsp;response\n	&nbsp;*&nbsp;@return\n	&nbsp;*&nbsp;@throws&nbsp;Exception\n	&nbsp;*/\n	@RequestMapping(&quot;/delete&quot;)\n	public&nbsp;String&nbsp;delete(@RequestParam(value=&quot;ids&quot;)String&nbsp;ids,HttpServletResponse&nbsp;response)\n			throws&nbsp;Exception{\n		String&nbsp;[]idsStr=ids.split(&quot;,&quot;);\n		for(int&nbsp;i=0;i&lt;idsStr.length;i++){\n			blogService.delete(Integer.parseInt(idsStr[i]));\n			blogIndex.deleteIndex(idsStr[i]);&nbsp;//&nbsp;删除对应博客的索引\n		}\n		JSONObject&nbsp;result=new&nbsp;JSONObject();\n		result.put(&quot;success&quot;,&nbsp;true);\n		ResponseUtil.write(response,&nbsp;result);\n		return&nbsp;null;\n	}\n	\n	/**\n	&nbsp;*&nbsp;通过ID查找实体\n	&nbsp;*&nbsp;@param&nbsp;id\n	&nbsp;*&nbsp;@param&nbsp;response\n	&nbsp;*&nbsp;@return\n	&nbsp;*&nbsp;@throws&nbsp;Exception\n	&nbsp;*/\n	@RequestMapping(&quot;/findById&quot;)\n	public&nbsp;String&nbsp;findById(@RequestParam(value=&quot;id&quot;)String&nbsp;id,HttpServletResponse&nbsp;response)\n			throws&nbsp;Exception{\n		Blog&nbsp;blog=blogService.findById(Integer.parseInt(id));\n		JSONObject&nbsp;jsonObject=JSONObject.fromObject(blog);\n		ResponseUtil.write(response,&nbsp;jsonObject);\n		return&nbsp;null;\n	}\n	\n	\n	\n}</pre><p><br/></p>', '1', '');
INSERT INTO `t_blog` VALUES ('85', 'eureka集群心跳配置与服务状态变更', '一、修改C:\\Windows\\System32\\drivers\\etc下的hosts文件 127.0.0.1 slave1 slave2 二、yml添加环境库,profiles: slave1,profiles: slave2,服务地址修改为http://slave2:8762/eureka、http://s', '2018-09-12 11:32:01', '11', '1', '<p><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">一、修改C:\\Windows\\System32\\drivers\\etc下的hosts文件 127.0.0.1 slave1 slave2&nbsp;</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">二、yml添加环境库,profiles: slave1,profiles: slave2,服务地址修改为</span><a href=\"http://slave2:8762/eureka\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none; font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">http://slave2:8762/eureka</a><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">、</span><a href=\"http://slave1:8761/eureka\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none; font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\">http://slave1:8761/eureka</a><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\"><span class=\"Apple-converted-space\">&nbsp;</span>用于服务的Eureka的互相注册</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">yml的一些配置项说明：</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">register-with-eureka：默认为ture 是否将服务注册信息提供给其他人发现</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">fetch-registry：是否从eureka服务器获取注册表信息</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">enable-self-preservation：默认为ture 用来保护eureka实例</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">eviction-interval-timer-in-ms：定时器间隔执行时间</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">leaseExpirationDurationInSeconds：eureka客户端向eureka服务器发送心跳的频率</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">com.netflix: DEBUG：开启debug模式 记录心跳</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">服务启动后，eureka客户端会通过配置的leaseExpirationDurationInSeconds向eureka服务器发送心跳频率，如果在指定的配置时间内没有续约请求，会将这个服务实力从eureka的列表中去除，默认时间为30秒，但是eureka里面有一个自我保护模式，需要通过配置enable-self-preservation来关掉自我保护模式，从eureka列表中清除服务，需要有两个条件，第一个条件就是，在指定的时间内没有任何请求，第二个条件是，必须达到eviction-interval-timer-in-ms的定时器的执行时间才会清理，没到5秒，服务列表不会发生更新</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">eureka server 在服务进行注册的时候，会对每一个服务进行注册，一旦开启两个或者两个以上的eureka server，会注册双份</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">eureka server 在关掉以后，不影响rpc的调用，服务跟服务可以被直接调用</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><span style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; background-color: rgb(255, 255, 255);\">心跳记录内容如下:</span><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/><br style=\"font-family: punctuation, 微软雅黑, Tohoma; font-size: 14px; white-space: normal; background-color: rgb(255, 255, 255);\"/></p><p>2018-07-18 12:03:43.123 DEBUG 1940 --- [tbeatExecutor-0] c.n.d.shared.MonitoredConnectionManager &nbsp;: Get connection: {}-&gt;<a href=\"http://localhost:8761/\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://localhost:8761</a>, timeout = 5000</p><p>2018-07-18 12:03:43.123 DEBUG 1940 --- [tbeatExecutor-0] c.n.d.shared.NamedConnectionPool &nbsp; &nbsp; &nbsp; &nbsp; : [{}-&gt;<a href=\"http://localhost:8761/\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://localhost:8761</a>] total kept alive: 1, total issued: 0, total allocated: 1 out of 200</p><p>2018-07-18 12:03:43.123 DEBUG 1940 --- [tbeatExecutor-0] c.n.d.shared.NamedConnectionPool &nbsp; &nbsp; &nbsp; &nbsp; : Getting free connection [{}-&gt;<a href=\"http://localhost:8761/\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://localhost:8761</a>][null]</p><p>2018-07-18 12:03:43.145 DEBUG 1940 --- [tbeatExecutor-0] c.n.d.shared.MonitoredConnectionManager &nbsp;: Released connection is reusable.</p><p>2018-07-18 12:03:43.146 DEBUG 1940 --- [tbeatExecutor-0] c.n.d.shared.NamedConnectionPool &nbsp; &nbsp; &nbsp; &nbsp; : Releasing connection [{}-&gt;<a href=\"http://localhost:8761/\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://localhost:8761</a>][null]</p><p>2018-07-18 12:03:43.146 DEBUG 1940 --- [tbeatExecutor-0] c.n.d.shared.NamedConnectionPool &nbsp; &nbsp; &nbsp; &nbsp; : Pooling connection [{}-&gt;<a href=\"http://localhost:8761/\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://localhost:8761</a>][null]; keep alive indefinitely</p><p>2018-07-18 12:03:43.146 DEBUG 1940 --- [tbeatExecutor-0] c.n.d.shared.NamedConnectionPool &nbsp; &nbsp; &nbsp; &nbsp; : Notifying no-one, there are no waiting threads</p><p>2018-07-18 12:03:43.146 DEBUG 1940 --- [tbeatExecutor-0] n.d.s.t.j.AbstractJerseyEurekaHttpClient : Jersey HTTP PUT<span class=\"Apple-converted-space\">&nbsp;</span><a href=\"http://localhost:8761/eureka//apps/EK-PROVIDER/PC-20180625AUVR:ek-provider\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://localhost:8761/eureka//apps/EK-PROVIDER/PC-20180625AUVR:ek-provider</a>; statusCode=200</p><p>2018-07-18 12:03:43.146 DEBUG 1940 --- [tbeatExecutor-0] com.netflix.discovery.DiscoveryClient &nbsp; &nbsp;: DiscoveryClient_EK-PROVIDER/PC-20180625AUVR:ek-provider - Heartbeat status: 200<br/><span style=\"margin: 0px; padding: 0px;\"><br/>获取服务实例 与服务id<br/><br/>eureka被关掉以后，无法获取服务id和服务实例<br/></span>DiscoveryClient ,向spring容器中注入&nbsp;DiscoveryClient类，可以通过&nbsp;DiscoveryClient类可以获得全部的服务id和服务实例<br/>discoveryClient.getServices(); return list&lt;String&gt;<br/>discoveryClient.getInstances(serviceId); &nbsp;return List&lt;ServiceInstance&gt;<br/>discoveryClient.getInstances(&quot;ek-provider&quot;);<br/>ins.getMetadata().get(&quot;company-name&quot;); 获取 &nbsp;yml &nbsp;company-name: crazyit<br/><br/>在控制层提供修改状态的方法<br/><br/><br/><span style=\"margin: 0px; padding: 0px;\">HealthIndicator：创建服务监视类，实现HealthIndicator接口，用于修改服务状态<br/><br/><br/>Spring Boot Actuator&nbsp;</span>health端点可用于查看服务状态 env用于查看端点信息<span style=\"margin: 0px; padding: 0px;\"><br/></span></p><p><a href=\"http://localhost:8080/env\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://localhost:8080/env</a><span class=\"Apple-converted-space\">&nbsp;</span>查看端点信息<span style=\"margin: 0px; padding: 0px;\"><br/></span></p><p><a href=\"http://127.0.0.1:8080/health\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://127.0.0.1:8080/health</a><span class=\"Apple-converted-space\">&nbsp;</span>查看状态<br/><br/><span style=\"margin: 0px; padding: 0px;\"><br/>Eureka Server与Eureka Client之间使用心跳机制来确定Eureka Client的状态，默认情况下，服务器端与客户端的心跳保持正常，应用程序就会始终保持“UP”状态，所以微服务的UP状态并不是应用程序的实际状态,所以需要实现HealthCheckHandler接口<br/><br/><br/></span></p><p>&nbsp;查看服务状态</p><p><br/></p><p><br/>修改服务状态<br/><span style=\"margin: 0px; padding: 0px;\"><a href=\"http://127.0.0.1:8080/db/true\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://127.0.0.1:8080/db/true</a><span class=\"Apple-converted-space\">&nbsp;</span>开启服务<br/><a href=\"http://127.0.0.1:8080/db/false\" target=\"_blank\" style=\"color: rgb(37, 149, 183); margin: 0px; padding: 0px; text-decoration-line: none;\">http://127.0.0.1:8080/db/false</a><span class=\"Apple-converted-space\">&nbsp;</span>关闭服务<br/><br/><br/><span style=\"margin: 0px; padding: 0px;\">根据yml配置的心跳信息，</span></span>服务状态被修改为down后，服务被定时器从列表中移除<span style=\"margin: 0px; padding: 0px;\"><span style=\"margin: 0px; padding: 0px;\"><br/></span>leaseExpirationDurationInSeconds：5 &nbsp;心跳频率<br/>eviction-interval-timer-in-ms ：5000<span style=\"margin: 0px; padding: 0px;\">&nbsp; 定时器执行时间</span></span></p><p><br/></p>', '1', '');

